
var coordinateX=$("#map-coordinates").attr("coordinate-x");var coordinateY=$("#map-coordinates").attr("coordinate-y");function initMap(coordinateX,coordinateY,map){var map;map=new google.maps.Map(document.getElementById('map'),{zoom:14,scrollwheel:false,center:new google.maps.LatLng(coordinateX,coordinateY),mapTypeId:'roadmap'});var iconBase='http://revolt.fresh-d.net/assets/images/';var icons={revolt:{icon:iconBase+'map_marker.png'}};var contentString=$('#map-coordinates').text();var infowindow=new google.maps.InfoWindow({content:contentString});var features=[{position:new google.maps.LatLng(coordinateX,coordinateY),type:'revolt'}];features.forEach(function(feature){var marker=new google.maps.Marker({position:feature.position,icon:icons[feature.type].icon,map:map});marker.addListener('click',function(){infowindow.open(map,marker);});});}
$(document).ready(function(){console.log($("#map").length>0);if($("#map").length>0){var coordinateX=$("#map-coordinates").attr("coordinate-x");var coordinateY=$("#map-coordinates").attr("coordinate-y");initMap(coordinateX,coordinateY);}});